import React from 'react';
import { currentValueOfTextArea, oneByOneCharachtersEnteringActionCreator, addNewPostActionCreator } from "../../../redux/ProfileReducer";
import Post from './Profile/Post/Post'
import Profile from './Profile/Profile'


let mapStateToProps = (state) => {
    return {
        posts: store.getState().Profile.posts.map((post, i) => <Post key={i} text={post.text} />).reverse(),
        currentValueOfTextArea: currentValueOfTextArea
    }
}

let mapDispatchToProps = (dispatch) => {
    return {
        callAddNewPostActionCreator: dispatch(addNewPostActionCreator(currentValueOfTextArea)),
        callOneByOneCharachtersEnteringActionCreator: dispatch(oneByOneCharachtersEnteringActionCreator(currentValueOfTextArea))
    }
}

const ProfileContainer = connect(mapStateToProps, mapDispatchToProps)(<Profile />)


export default ProfileContainer;
