import React from "react";
import stylesheet from './Profile.module.css'
import Post from './Post/Post'
import { currentValueOfTextArea, callAddNewPostActionCreator } from "../../../redux/ProfileReducer";

const Profile = (props) => {

    let posts = props.Profile.posts.map((item, i) => <Post id={item.id} />)

    let textAreaConverterHTMLtoJS = React.createRef();

    return (
        <div className={stylesheet.Prolile}>
            <div><textarea ref={textAreaConverterHTMLtoJS} value={currentValueOfTextArea} /></div>
            <div>{posts}</div>
        </div>
    )
}

export default Profile;
