import React from 'react';
import stylesheet from './Slider.module.css'
import * as axios from 'axios'



const Slider = (props) => {



    let posts = props.posts;

    const getNextPost = () => {

        // load data at the start from db.json

        props.isFetchingToggle(true);
        props.updateSlider();

        let startCount = props.startCount;
        let endCount = startCount + 3;

        let frame = document.querySelector(`.${stylesheet.frame}`)


        if (frame.style.top === "-450px") {

            axios
                .get(`http://localhost:3001/posts?_start=${startCount}&_end=${endCount}`)
                .then(response => {
                    props.isFetchingToggle(false)
                    props.setPosts(response.data)
                    frame.style.top = "0px"
                    frame.style.transition = null;
                    setTimeout(() => {
                        frame.style.top = "-450px"
                        frame.style.transition = "top 0.2s ease-out 0s";
                    }, 50)
                });
        } else {
            frame.style.top = "-450px"
            frame.style.transition = "top 0.2s ease-out 0s";
            props.isFetchingToggle(false)
        }
    }

    const getPreviousPost = () => {

        // load data at the start from db.json

        props.isFetchingToggle(true);
        props.comebackSlider();

        let startCount = props.startCount - 2;
        let endCount = startCount + 3;

        let frame = document.querySelector(`.${stylesheet.frame}`)


        if (startCount >= 0) {

            axios
                .get(`http://localhost:3001/posts?_start=${startCount}&_end=${endCount}`)
                .then(response => {
                    props.isFetchingToggle(false)
                    props.setPosts(response.data)
                    frame.style.top = "-900px"
                    frame.style.transition = null;
                    setTimeout(() => {
                        frame.style.top = "-450px"
                        frame.style.transition = "top 0.2s ease-out 0s";
                    }, 50)
                });
        } else {
            frame.style.top = "0px"
            frame.style.transition = "top 0.2s ease-out 0s";
            setTimeout(() => {
                frame.style.top = "100px"
                setTimeout(() => {
                    frame.style.top = "0px"
                }, 50);
            }, 50);

            props.isFetchingToggle(false)
        }
    }

    let sum = props.sum;
    const incrementSum = props.incrementSum;

    const onWheel = (e) => {
        if (e.deltaY > 0) {
            incrementSum(1);
        } else if (e.deltaY < 0) {
            incrementSum(-1);
        }

        if (sum === 3) {
            getNextPost();
        } else if (sum === -3) {
            getPreviousPost();
        }
    }



    return (
        <div onWheel={onWheel} className={stylesheet.sliderWrapper}>
            <div className={stylesheet.slider}>
                <div className={stylesheet.frame}>
                    {posts}
                </div>
            </div>
        </div>
    )
}

export default Slider;
