import React from 'react';
import stylesheet from './Post.module.css'
import Comment from './Comment/Comment'
import { callAddCommentActionCreator } from './../../../../redux/PostReducer'

let Post = (props) => {

    let comments = props.post.comments.map((comment, i) => <Comment key={i} postId={props.post.id} comment={comment} />)

    console.log(props.key)
    console.log(textAreaConverterHTMLtoJS.current.value)

    let textAreaConverterHTMLtoJS = React.createRef();

    let commentProperties = {
        keyParentPost: props.key,
        desirableValue: textAreaConverterHTMLtoJS.current.value
    }



    let addCommentActionCreator = () => {
        props.dispatch(callAddCommentActionCreator(commentProperties));
    }

    return (
        <div className={stylesheet.Post}>
            <div>
                {props.post.text}
            </div>
            <br />
            <div>
                <p>Likes: </p>{props.post.likes}
            </div>
            <div>
                <p>Comments:</p>
                <br />
                <textarea ref={textAreaConverterHTMLtoJS} placeholder='Share your opinion here...' />
                {/* <burron onClick={addCommentActionCreator}>Send</burron> */}
                <br />
                <div>
                    {comments}
                </div>
            </div>
        </div>
    )
}

export default Post;
