import chats from './redux/data/inboxItems.json';
import InboxReducer from './redux/InboxReducer';

const ONE_BY_ONE_CHARACHTERS_ENTERING = 'ONE-BY-ONE-CHARACHTERS-ENTERING';
const ADD_NEW_MESSAGE = 'ADD-NEW-MESSAGE'





export let store = {
    _state: {
        Inbox: {
            Person: chats,
            Messages: [
                {
                    id: "1",
                    text: "- It's not easy to live your own way."
                },
                {
                    id: "2",
                    text: "Because you can't blame anybody but yourself."
                },
                {
                    id: "3",
                    text: "- I don't want to be a burden! I want to be useful."
                }
            ]
        },
        Profile: {
            posts: [
                {
                    id: '1',
                    text: `Hey, world! I use a LC to save a moments dear to my heart.` 
                }
            ]
        }
    },
    currentValueOfTextArea: '',
    _reloadApp() { },
    subscriber(observer) {
        this._reloadApp = observer;
    },
    getState() {
        return this._state;
    },
    getDefaultText() {
        return this.currentValueOfTextArea;
    },
    dispatch(action) {
        this._state.Inbox = InboxReducer(this._state.Inbox)
        this._state.Profile = ProfileReducer(this._state.Profile)
    }
}
