import React from 'react';
import stylesheet from './Slider.module.css'
import * as axios from 'axios'
import Loading from '../../common/Loading/Loading'



const Slider = (props) => {

    let state = props.state;

    const getNextPost = () => {

        // load data at the start from db.json

        state.isFetchingToggle(true);
        state.updateSlider();

        let startCount = state.startCount;
        let endCount = startCount + 3;

        let frame = document.querySelector(`.${stylesheet.frame}`)


        if (frame.style.top === "-450px") {

            axios
                .get(`http://localhost:3001/~?_start=${startCount}&_end=${endCount}`)
                .then(response => {
                    state.isFetchingToggle(false)
                    state.setPosts(response.data)
                    frame.style.top = "0px"
                    frame.style.transition = null;
                    setTimeout(() => {
                        frame.style.top = "-450px"
                        frame.style.transition = "top 0.2s ease-out 0s";
                    }, 50)
                });
        } else {
            frame.style.top = "-450px"
            frame.style.transition = "top 0.2s ease-out 0s";
            state.isFetchingToggle(false)
        }
    }

    const getPreviousPost = () => {

        // load data at the start from db.json

        state.isFetchingToggle(true);
        state.comebackSlider();

        let startCount = state.startCount - 2;
        let endCount = startCount + 3;

        let frame = document.querySelector(`.${stylesheet.frame}`)


        if (startCount >= 0) {

            axios
                .get(`http://localhost:3001/posts?_start=${startCount}&_end=${endCount}`)
                .then(response => {
                    state.isFetchingToggle(false)
                    state.setPosts(response.data)
                    frame.style.top = "-900px"
                    frame.style.transition = null;
                    setTimeout(() => {
                        frame.style.top = "-450px"
                        frame.style.transition = "top 0.2s ease-out 0s";
                    }, 50)
                });
        } else {
            frame.style.top = "0px"
            frame.style.transition = "top 0.2s ease-out 0s";
            setTimeout(() => {
                frame.style.top = "100px"
                setTimeout(() => {
                    frame.style.top = "0px"
                }, 50);
            }, 50);

            state.isFetchingToggle(false)
        }
    }

    let sum = state.sum;
    const incrementSum = state.incrementSum;

    const onWheel = (e) => {
        if (e.deltaY > 0) {
            incrementSum(1);
        } else if (e.deltaY < 0) {
            incrementSum(-1);
        }

        if (sum === 3) {
            getNextPost();
        } else if (sum === -3) {
            getPreviousPost();
        }
    }

    let posts = state.posts;

    return (
        <div>
            <div className={stylesheet.loading}>
                {state.isFetching ? <Loading /> : null}
            </div>
            <div onWheel={onWheel} className={stylesheet.sliderWrapper}>
                <div className={stylesheet.slider}>
                    <div className={stylesheet.frame}>
                        {posts}
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Slider;
