const SECTION_TOGGLE = 'SECTION-TOGGLE';
const IS_FETCHING = 'IS-FETCHING';


const initialState = {
    posts: [],
    currentSection: 'Recommendations',
    isFetching: false
}

export const HomeReducer = (state = initialState, action) => {
    switch (action.type) {
        case SECTION_TOGGLE:
            return {
                ...state,

            }
        case IS_FETCHING:
            return {
                ...state,
                isFetching: !action.isFetching
            }
        default:
            return state;
    }
}


export const currentSectionToggle = (currentSection) => ({ type: SECTION_TOGGLE, })
export const isFetchingToggle = (isFetching) => ({ type: IS_FETCHING, isFetching: isFetching })
