import * as axios from 'axios';

const instance = axios.create({
    baseURL: 'http://localhost:3001/'
});


export const ProfileAPI = {
    getPosts() {
        axios
            .get('http://localhost:3001/posts')
            .then(response => response.data)
    }
};

