import React from 'react';
import ReactDOM from 'react-dom/client';
import { BrowserRouter } from 'react-router-dom';
import { state} from './state'
import App from './App'

const root = ReactDOM.createRoot(document.getElementById('root'));


export let addMessage = (message) => {
    let count = state.Inbox.InboxMessages.length;
    state.Inbox.InboxMessages[count] = { id: `${count + 1}`, text: `${message}` }
}

let defaultText = 'Write here what you want to say...';
let singleCharacterEnter = (updatedString) => {
    defaultText = updatedString;
}




export let loadPage = (x) => {
    root.render(
        <BrowserRouter>
            <App inboxState={x} defaultText={defaultText} singleCharacterEnter={singleCharacterEnter} addMessage={addMessage} />
        </BrowserRouter>
    );
}

loadPage(state);
