export const ADD_NEW_COMMENT_TO_THE_POST = 'ADD-NEW-COMMENT-TO-THE-POST';

export let currentValueOfTextArea = '';

export let PostReducer = (state, action) => {
    switch (action.type) {
        case ADD_NEW_COMMENT_TO_THE_POST:
            let count = state.posts.comments[action.postId].length;
            let comment = {
                id: `"${count}"`,
                text: `"${action.desirableValue}"`
            };
            state.posts.somments.push(comment);
            return state;
        default:
            return state;
    }

}

export const callAddCommentActionCreator = (action) => ({ type: ADD_NEW_COMMENT_TO_THE_POST, postId: action.idParentPost, desirableValue: action.desirableValue})
